mapper.select:
select id, title, author, date, content, category_id
from post
where id=#{value}

select id, title, author, date, content, category_id
from post
where category_id=#{value}

mapper.update:
update post
set title=#{title}, author=#{author}, date=now(), content=#{content}, category_id=#{categoryId}
where id=#{id}
