mapper.select:
select id, title, author, date, markdown_content, category_id, html_content
from post
where id=#{value}

select id, title, author, date, markdown_content, category_id, html_content
from post
where category_id=#{value}

mapper.update:
update post
set title=#{title}, author=#{author}, date=now(), markdown_content=#{markdownContent}, category_id=#{categoryId},
html_content=#{htmlContent}
where id=#{id}
